import React, { useState } from "react";
import classes from './AddUsers.module.css'
import Card from "../UI/card";
import Button from "../UI/button";
import ErrorModal from "../UI/errorModal";


const Addusers = (props)=>{

    const [enteredUsername, SetEnteredUsername] = useState('');
    const [enteredAge, SetEnteredAge] = useState('');
    const [error, setError] =useState('');



    const usernameChangeHandler = (event) =>{

        SetEnteredUsername(event.target.value);
    }

    const ageChangeHandler= (event) => {
        SetEnteredAge(event.target.value);
    }

    const AddUserHandler = event =>{
        event.preventDefault();
        if(enteredUsername.trim().length ===0 || enteredAge.trim().length === 0){
            setError({
                title:'Empty field',
                message :'Enter name and age'

            })
            return;
        }

        if(enteredAge<0){
            setError({
                title:'Invalid age',
                message:'Enter age greater than 0'
            })
            return;
        }

       props.onAddUser(enteredUsername, enteredAge);
       SetEnteredAge('');
       SetEnteredUsername('')
    } 
    const AddErrorHandler = () => {
        setError('');
    }



    return(
        <div>
            {error && <ErrorModal title = {error.title} message = {error.message} onConfirm = {AddErrorHandler}/>}
       <Card className = {classes.input}>
         <form onSubmit={AddUserHandler}>
            <label htmlFor="username">Username</label>
            <input id="username" type="text" onChange={usernameChangeHandler} value={enteredUsername}></input>
            <label htmlFor="age">Age(in years)</label>
            <input id="age" type="number" onChange={ageChangeHandler} value={enteredAge} ></input>
            <Button type="submit"> Add</Button>
        </form>
       </Card>
       </div>
    )
}

export default Addusers;
